mapper.select:
SELECT * FROM INV

	
		AND INV_CAT_NO = #{ invCatNo }
	
	
		AND INV_NO = #{ invNo }
	
		
		AND INV_NAME LIKE '%' || #{ invName } || '%'
	

ORDER BY INV_NO DESC

SELECT COUNT(*) FROM INV
	
		
		AND INV_CAT_NO = #{ invCatNo }
	
	
		AND INV_NO = #{ invNo }
	
		
		AND INV_NAME LIKE '%' || #{ invName } || '%'
	
	
	ORDER BY INV_NO DESC

SELECT COUNT(*) FROM INV
	
		
		AND INV_CAT_NO = #{ invCatNo }
	
	
		AND INV_NO = #{ invNo }
	
		
		AND INV_NAME LIKE '%' || #{ invName } || '%'
