mapper.select:
SELECT * FROM ORDER_ENTER

	
		AND OE_NAME LIKE '%' || #{ oeName } || '%'
	
	
		AND OE_NO = #{ oeNo }
	
	
		AND ORDER_DATE = #{ orderDate1 }
	
	
		AND ORDER_DATE BETWEEN #{ orderDate1 } AND #{ orderDate2 }
	

ORDER BY OE_NO DESC

mapper.update:
UPDATE ORDER_ENTER
SET OE_STATUS = 'Y', ENTER_DATE = SYSDATE
WHERE OE_NO = #{ oeNo } AND OE_NAME = #{ oeName }

mapper.select:
SELECT OE_INV_NO
FROM ORDER_ENTER
WHERE OE_NO = #{ oeNo } AND OE_NAME = #{ oeName }

SELECT OE_INV_NO, OE_NAME, OE_PRICE_E, ORDER_DATE
FROM ORDER_ENTER
WHERE OE_INV_NO = #{oeInvNo}
