mapper.select:
SELECT COUNT(*)
FROM  PRODUCT P, SALE S
	
	SALE_NAME = PRO_NAME
		
			AND SALE_NAME LIKE '%' || #{ saleName } || '%'
		
		
			AND PRO_NO = #{ proNo }
		
		
			AND SALE_DATE = #{ saleDate1 }
		
		
			AND SALE_DATE BETWEEN #{ saleDate1 } AND #{ saleDate2 }

		SELECT S.*, P.PRO_NO
		FROM PRODUCT P, SALE S
		 
		SALE_NAME = PRO_NAME
			
				AND SALE_NAME LIKE '%' || #{ saleName } || '%'
			
			
				AND PRO_NO = #{ proNo }
			
			
				AND SALE_DATE = #{ saleDate1 }
			
			
				AND SALE_DATE BETWEEN #{ saleDate1 } AND #{ saleDate2 }
